import React, { useState, useEffect } from 'react'
import { Flex, Heading, Input, Grid, GridItem, Button, Badge, Text, Box, Image } from '@chakra-ui/react'
import * as PokemonActions from '../actions/pokemonSearch.actions';
import { MdStar } from "react-icons/md";

const PokeDex = () => {
    const [pokemonName, setPokemonName] = useState('');
    const [pokemonData, setPokemonData] = useState([]);

    const handlePokemonName = (event) => {
        setPokemonName(event.target.value);
        console.log(pokemonName);
    }

    const searchPokemon = async (name) => {
        const result = await PokemonActions.getPokemonInfo(name.toLowerCase());
        console.log(result);
        setPokemonData(result);
        //console.log('pokemon data', pokemonData);
    }

    return (
        <Flex height='100vh' alignItems='center' justifyContent='center'>
            <Flex height='80%' width='80%' background='red.500' p={10} rounded={6}>
                <Grid templateColumns="repeat(5, 1fr)" w='100%' gap={4}>
                    <GridItem rounded={5} colSpan={3} h="100%" bg="white" p={4}>
                        <Heading>PokeDex</Heading>
                        <Input onChange={handlePokemonName} placeholder="Pikachu" variant='filled' mb={3} />
                        <Button onClick={() => searchPokemon(pokemonName)} colorScheme='green'>Search</Button>
                        <div style={{ marginTop: '20px' }}>
                            {
                                pokemonData.length != 0 ?
                                    <Box p="5" maxW="320px" borderWidth="2px">
                                        <Image h='210px' borderRadius="md" src={pokemonData[5]} />
                                        <Text mt={2} fontSize="xl" fontWeight="semibold" lineHeight="short">
                                            {pokemonData[3]}
                                        </Text>
                                        <Flex align="baseline" mt={2}>
                                            <Badge colorScheme="pink">{pokemonData[6]}</Badge>
                                            <Text
                                                ml={2}
                                                textTransform="uppercase"
                                                fontSize="sm"
                                                fontWeight="bold"
                                                color="pink.800"
                                            >
                                                Verified &bull; Cape Town
                                            </Text>
                                        </Flex>

                                        <Text mt={2}>$119/night</Text>
                                        <Flex mt={2} align="center">
                                            <Box as={MdStar} color="orange.400" />
                                            <Text ml={1} fontSize="sm">
                                                <b>4.84</b> (190)
                                            </Text>
                                        </Flex>
                                    </Box>
                                    : <Heading>ZERO</Heading>
                            }
                        </div>
                    </GridItem>

                    <GridItem rounded={5} colSpan={2} h="100%" bg="white" />
                </Grid>
            </Flex>
        </Flex>
    )
}

export default PokeDex
