import React, { useState, useEffect } from 'react'
import { Flex, Heading, Center, Stack, Input, useToast, Grid, GridItem, Button, Badge, Text, Box, Image, useColorMode, useColorModeValue, extendTheme } from '@chakra-ui/react'
import * as PokemonActions from '../actions/pokemonSearch.actions';
import '../App.css';

const PokemonCard = ({ name, id, xp, height, weight, image, type }) => {

    return (
        <div>
            <Center py={4}>
                <Box
                    role={'group'}
                    p={6}
                    maxW={'300px'}
                    w={'full'}
                    className='background-card'
                    boxShadow={'2xl'}
                    rounded={'lg'}
                    pos={'relative'}
                    zIndex={1}>
                    <div className='pokemon-card'>
                        <Box
                            rounded={'lg'}
                            mt={-12}
                            pos={'relative'}
                            height={'230px'}
                        >
                            <Image
                                rounded={'lg'}
                                height={150}
                                width={150}
                                objectFit={'cover'}
                                src={image}
                                className='pokemon-img'
                                style={{ position: 'absolute', left: '33px', top: '63px' }}
                            />
                        </Box>
                    </div>
                    <Stack pt={4} align={'center'}>
                        <Heading className='pokemon-name' fontSize={'3xl'} fontFamily={'body'} fontWeight={600}>
                            {name.toUpperCase()}
                        </Heading>
                        <Badge colorScheme="pink">{type}</Badge>
                        <div className='info-stack'>
                            <Stack style={{ display: 'inline-block', margin: '5px' }} direction={'column'} align={'center'}>
                                <Text fontWeight={800} fontSize={'m'}>
                                    ID#
                                </Text>
                                <Text fontWeight={800} color={'gray.700'}>
                                    {id}
                                </Text>
                            </Stack>
                            <Stack style={{ display: 'inline-block', margin: '5px' }} direction={'column'} align={'center'}>
                                <Text fontWeight={800} fontSize={'m'}>
                                    XP
                                </Text>
                                <Text fontWeight={800} color={'gray.700'}>
                                    {xp}
                                </Text>
                            </Stack>
                            <Stack style={{ display: 'inline-block', margin: '5px' }} direction={'column'} align={'center'}>
                                <Text fontWeight={800} fontSize={'m'}>
                                    Height
                                </Text>
                                <Text fontWeight={800} color={'gray.700'}>
                                    {height}
                                </Text>
                            </Stack>
                            <Stack style={{ display: 'inline-block', margin: '5px' }} direction={'column'} align={'center'}>
                                <Text fontWeight={800} fontSize={'m'}>
                                    Weight
                                </Text>
                                <Text fontWeight={800} color={'gray.700'}>
                                    {weight}
                                </Text>
                            </Stack>
                        </div>


                    </Stack>
                </Box>

            </Center>
        </div>
    )
}

export default PokemonCard
