import React, { useState, useEffect } from 'react'
import { Flex, Heading, Center, Stack, Input, useToast, Grid, GridItem, Button, Badge, Text, Box, Image, useColorMode, useColorModeValue, extendTheme } from '@chakra-ui/react'
import * as PokemonActions from '../actions/pokemonSearch.actions';
import '../App.css';
import { MdStar } from "react-icons/md";

//var myTeam = [];
const PokeDex = () => {
    const theme = extendTheme({
        colors: {
            brand: {
                50: "#44337A",
                100: "#B794F4",
                500: "#B794F4", // you need this
            }
        }
    });

    const [pokemonName, setPokemonName] = useState('');
    const [pokemonData, setPokemonData] = useState([]);
    const [myTeam, setMyTeam] = useState([]);
    const [pokemonClicked, setPokemonClicked] = useState(false);
    const [pokemonHighlightColor, setPokemonHighlightColor] = useState('#ababab');
    const toast = useToast()

    const handlePokemonName = (event) => {
        setPokemonName(event.target.value);
        //console.log(pokemonName);
        console.log(pokemonData)
    }

    const searchPokemon = async (name) => {
        setPokemonHighlightColor('#ababab')
        const result = await PokemonActions.getPokemonInfo(name.toLowerCase());
        console.log(result);
        setPokemonData(result);
        //console.log('pokemon data', pokemonData);
    }

    const handlePokemonClick = () => {
        setPokemonHighlightColor('#1cc50d')
    }

    const myTeamClicked = (event) => {
        console.log(event);
    }

    const addPokemon = () => {
        if (myTeam.length != 2) {
            setMyTeam(myTeam => [...myTeam, pokemonData]);
        } else {
            toast({
                position: "top",
                title: "Error",
                description: "Your PokeMon team is full!",
                status: "error",
                duration: 4000,
                isClosable: true,
            })
        }

        console.log(myTeam);
    }

    const deletePokemon = (index) => {
        console.log('deleting', index);
        var array = [...myTeam];
        toast({
            position: "top",
            title: "Success",
            description: `Removing ${myTeam[index][3]} from your team.`,
            status: "success",
            duration: 4000,
            isClosable: true,
        })
        array.splice(index, 1);
        console.log(array);

        setMyTeam(array);

    }

    return (
        <div className='container' height='100vh'>
            <Flex className='pokedex-container' alignItems='center' p={8}>
                <GridItem bg='red.500' className='inner-pokedex' p={3}>
                    <Input onChange={handlePokemonName} w='300px' className='search' placeholder="Pikachu" variant='filled' mb={3} />
                    <Button onClick={() => searchPokemon(pokemonName)} colorScheme='green'>Search</Button>
                    <div style={{ height: '400px', position: 'relative' }}>
                        {
                            pokemonData.length != 0 ?
                                <Center py={8} pos={'relative'}>
                                    <Box
                                        role={'group'}
                                        p={6}
                                        maxW={'280px'}
                                        w={'full'}
                                        className='background-card'
                                        boxShadow={'2xl'}
                                        rounded={'lg'}
                                        pos={'relative'}
                                        zIndex={1}>
                                        <div className='pokemon-card'>
                                            <Box
                                                rounded={'lg'}
                                                mt={-12}
                                                pos={'relative'}
                                                height={'230px'}
                                            >
                                                <Image
                                                    rounded={'lg'}
                                                    height={120}
                                                    width={120}
                                                    objectFit={'cover'}
                                                    src={pokemonData[5]}
                                                    className='pokemon-img'
                                                    style={{ position: 'absolute', left: '45px', top: '83px' }}
                                                />
                                            </Box>
                                        </div>
                                        <Stack pt={4} align={'center'}>
                                            <Heading className='pokemon-name' fontSize={'3xl'} fontFamily={'body'} fontWeight={600}>
                                                {pokemonData[3].toUpperCase()}
                                            </Heading>
                                            <Text color={'gray.500'} fontSize={'sm'} textTransform={'uppercase'}>
                                                {pokemonData[6]}
                                            </Text>

                                            <Stack direction={'row'} align={'center'}>
                                                <Text fontWeight={800} fontSize={'xl'}>
                                                    $57
                                                </Text>
                                                <Text textDecoration={'line-through'} color={'gray.600'}>
                                                    $199
                                                </Text>
                                            </Stack>
                                        </Stack>
                                    </Box>
                                    <Button onClick={addPokemon} style={{ position: 'relative' }} colorScheme='green'>Add to My Team</Button>
                                </Center>
                                : <Heading></Heading>
                        }
                    </div>

                </GridItem>

            </Flex>
            {/*<Flex height='80%' width='80%' background='red.500' p={10} rounded={6}>

                <Grid templateColumns="repeat(5, 1fr)" w='100%' gap={4}>
