                    </GridItem>
                    <GridItem className='my-team' rounded={5} colSpan={2} p={4} h="100%">
                        <div>
                            {myTeam.map((row, idx) => {
                                return (
                                    <div className='my-pokemon' key={row} onClick={() => myTeamClicked(idx)}>
                                        <Button onClick={() => deletePokemon(idx)}>X</Button>
                                        <Image h='150px' w='150px' borderRadius="md" src={row[5]} ml='25px' />
